import {LightningElement,api,wire,track} from 'lwc';
import {loadScript} from "lightning/platformResourceLoader";
import JSPDF from '@salesforce/resourceUrl/jspdf';
import { getRecord, getFieldValue } from "lightning/uiRecordApi";
const FIELDS = [
'Opportunity.Id','Opportunity.Name',  'Opportunity.Account.Name','Opportunity.Principal_Loan_Amount__c','Opportunity.Per_diem__c'
,'Opportunity.Discharged_Fee__c','Opportunity.Penalty_amount__c'

];

export default class JspdfJK extends LightningElement {

@track name;
@track accountName;
@track principalamount;
@track perdiem;
@track discharge;
@track penalty;
totalamount;
@api recordId;


connectedCallback(){
const formattedDate = new Date().toDateString();
const [, month, day, year] = formattedDate.split(' ')

const datelist = `${month} ${day}, ${year}`
this.date=datelist ;
}
@wire(getRecord, {
recordId: "$recordId",
fields:FIELDS
})
contactData({data,error}){
if(data){
let USDollar = new Intl.NumberFormat('en-US', {
style: 'currency',
currency: 'USD',
});
var regp = /[^0-9.-]+/g;

console.log('data'+JSON.stringify(data))
this.name=getFieldValue(data,'Opportunity.Name');
this.accountName=getFieldValue(data,'Opportunity.Account.Name')
this.principalamount=USDollar.format(getFieldValue(data,'Opportunity.Principal_Loan_Amount__c'));
this.perdiem=USDollar.format(getFieldValue(data,'Opportunity.Per_diem__c'));
this.discharge=USDollar.format(getFieldValue(data,'Opportunity.Discharged_Fee__c'));
this.penalty=USDollar.format(getFieldValue(data,'Opportunity.Penalty_amount__c'));
this.totalamount=USDollar.format(parseFloat(this.principalamount.replace(regp, '')) +parseFloat(this.perdiem.replace(regp, ''))+ parseFloat(this.discharge.replace(regp, ''))+ parseFloat(this.penalty.replace(regp, '')));
console.log('data'+this.principalamount)
}
else if(error){
console.log(error)
}
}
renderedCallback(){
if (this.jsPdfInitialized) {
return;
}
this.jsPdfInitialized = true;

Promise.all([
loadScript(this, JSPDF)
]);
}

generatePdf(){
var myImage2 = 'data:image/jpeg;base64,'
var myImage ='data:image/jpeg;base64,'
console.log('total amount'+this.totalamount)
console.log('generatePdf'+this.principalamount)
const { jsPDF } = window.jspdf;
const doc = new jsPDF({

});
doc.setFontSize(11);
doc.addImage(myImage, 'JPEG', 125, 5, 65, 25);
doc.addImage(myImage2, 'JPEG', 145, 40, 40, 20);
doc.text("To:", 30, 40)
doc.text("Cangeswary", 30, 50)

doc.text(this.date, 30, 65)
doc.setFont("Calibri", "bold");
doc.text("Re: SECOND Mortgage Discharge Statement ", 60, 80) 
doc.line(60, 80.5, 135, 80.5);
doc.text("Property: ", 60, 90)
doc.setFontSize(12);
doc.setFont("Calibri", "normal");
doc.text(this.accountName, 78, 90)
doc.setFontSize(11);
doc.text("The following is the statement of outstanding balance of the above cited SECOND MORTGAGE.", 30, 100)
doc.text("a) Principal as of "+this.date, 30, 120)
doc.text("b) Discharge Fee ", 30, 130)
doc.text("c)  Per diem charge ", 30, 140)
doc.text("d) Renewal penalty   ", 30, 150)
doc.text(": ", 100, 120)
doc.text(": ", 100, 130)
doc.text(": ", 100, 140)
doc.text(": ", 100, 150)
doc.text(String(this.principalamount), 130, 120, null, null, "right");
doc.text(String(this.discharge), 130, 130, null, null, "right");
doc.text(String(this.perdiem), 130, 140, null, null, "right");
doc.text(String(this.penalty), 130, 150, null, null, "right");
doc.text(String(this.totalamount), 130, 170, null, null,"right")
doc.line(110, 163, 135, 163);
doc.line(110, 177, 135, 177);
doc.text("Payment must be drawn in favour of “2639051 Ontario Inc” by certified cheque/draft only with" , 30, 185)
doc.text("applicable per diem. Per Diem is "+String(this.perdiem) , 30, 190)
doc.text("Yours Truly ", 30, 235)
doc.line(30, 250, 80, 250);
doc.setFont("Calibri", "bold");
doc.text("2639051 Ontario Inc ", 30, 260)
doc.save("Payout.pdf");
}

generateData(){
this.generatePdf();
}
@api invoke() {
//Here insert your code
console.log('Hello, world!');
this.generatePdf();
}



}


